        setInput2={setInput2}
        setSubscriptionType={setSubscriptionType}
        setInput4={setInput4}
        handleFormSubmit={handleFormSubmit}
        calculatePrice={calculatePrice}
      />
      <SubscriptionTable
      
      />
    </div>
    </>
  )
}

export default Subscription
