import Addnewspaper from './Addnewspaper'
import Table from './Table'

const Newspaper = () => {
  const API_URL = "http://localhost:5000/newspaper";
  const [items,setItems] = useState([]);
  const [input1, setInput1] = useState('');
  const [input2, setInput2] = useState('');

  useEffect(() => {
    const fetchItems = async() => {
      try{
        const response = await fetch(API_URL);
      
      }catch(err){

      }
    }
  })
  
  

  const setandSave = (newItem) => {
    setItems(newItem);
    localStorage.setItem('Newspapers', JSON.stringify(newItem));
  }
  const addItem = async (item) => {
    const listItems = [...items,item];
    setandSave(listItems);
  }

  const handleEdit = () =>{

  }

  const handleDelete = () => {

  }

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const item = {
      name: input1,
      category: input2
    }
    console.log([input1,input2]);
    addItem(item);
    setInput1('');
    setInput2('');
  }
  return (
    <>
    <h1 className='componentInfo'>Add Newspaper</h1>
    <div className='newspaper_component'>
      <Addnewspaper
       input1={input1}
       input2={input2}
       setInput1={setInput1}
       setInput2={setInput2}
       handleFormSubmit={handleFormSubmit}
      />
      <Table
        items={items}
        handleEdit={handleEdit}
        handleDelete={handleDelete}
      />
      </div>
    </>
  )
}

export default Newspaper
